import React, { useState, useEffect } from 'react';
import { Mic, MicOff, Camera, Volume2 } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';

const SpeechInterface = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [response, setResponse] = useState('');
  const [error, setError] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('en-IN');
  const [lastCapturedImage, setLastCapturedImage] = useState(null);
  const [status, setStatus] = useState('idle');

  // Check server status on component mount
  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/status');
      const data = await response.json();
      setStatus(data.status === 'Server is running' ? 'connected' : 'disconnected');
    } catch (err) {
      setStatus('disconnected');
      setError('Cannot connect to server');
    }
  };

  const handleStartRecording = async () => {
    setIsRecording(true);
    setError('');

    try {
      const response = await fetch('http://localhost:5002/api/recognize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          language: selectedLanguage,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setResponse(data.response);
        // Simulate receiving a new image (in real app, this would come from backend)
        setLastCapturedImage(`/api/placeholder/640/480?text=Last+Captured+Image&time=${Date.now()}`);
      } else {
        setError(data.error || 'Failed to recognize speech');
      }
    } catch (err) {
      setError('Failed to communicate with server');
    } finally {
      setIsRecording(false);
    }
  };

  const handleStopRecording = () => {
    setIsRecording(false);
  };

  const languages = [
    { code: 'en-IN', name: 'English' },
    { code: 'hi-IN', name: 'हिंदी' },
    { code: 'mr-IN', name: 'मराठी' },
    { code: 'ta-IN', name: 'தமிழ்' },
    { code: 'bn-IN', name: 'বাংলা' },
    // Add more languages as needed
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Status Card */}
        <Card className="bg-white">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              Smart Glasses Interface
              <span className={`px-3 py-1 rounded-full text-sm ${
                status === 'connected' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
              }`}>
                {status}
              </span>
            </CardTitle>
          </CardHeader>
        </Card>

        {/* Main Interface */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Camera Feed */}
          <Card className="bg-white">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Camera className="w-5 h-5" />
                Camera Feed
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="aspect-video bg-gray-200 rounded-lg overflow-hidden">
                {lastCapturedImage ? (
                  <img 
                    src={lastCapturedImage} 
                    alt="Last captured" 
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-gray-500">
                    No image captured
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Controls & Response */}
          <Card className="bg-white">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Volume2 className="w-5 h-5" />
                Voice Controls
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Language Selection */}
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="w-full p-2 border rounded-lg"
              >
                {languages.map((lang) => (
                  <option key={lang.code} value={lang.code}>
                    {lang.name}
                  </option>
                ))}
              </select>

              {/* Record Button */}
              <Button
                onClick={isRecording ? handleStopRecording : handleStartRecording}
                variant={isRecording ? "destructive" : "default"}
                className="w-full h-16 text-lg"
              >
                {isRecording ? (
                  <div className="flex items-center gap-2">
                    <MicOff className="w-6 h-6" />
                    Stop Recording
                  </div>
                ) : (
                  <div className="flex items-center gap-2">
                    <Mic className="w-6 h-6" />
                    Start Recording
                  </div>
                )}
              </Button>

              {/* Response Display */}
              {response && (
                <div className="p-4 bg-gray-50 rounded-lg">
                  <p className="font-medium">Response:</p>
                  <p className="mt-2">{response}</p>
                </div>
              )}

              {/* Error Display */}
              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default SpeechInterface;
