import React from 'react';
import { Card, CardContent } from './Card';

const ImageGallery = ({ images = [] }) => {
  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Recent Captures</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {images && images.length > 0 ? (
          images.map((image) => (
            <Card key={image.id} className="overflow-hidden">
              <CardContent className="p-0">
                <img
                  src={`http://localhost:5002/api/image/${image.filename}`}
                  alt={`Captured at ${image.timestamp}`}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <div className="text-sm text-gray-500 mb-2">
                    {image.timestamp}
                  </div>
                  <p className="font-medium mb-2">Prompt: {image.prompt}</p>
                  <p className="text-sm text-gray-700">{image.response}</p>
                </div>
              </CardContent>
            </Card>
          ))
        ) : (
          <p>No images available.</p>
        )}
      </div>
    </div>
  );
};

export default ImageGallery;
