// src/pages/SpeechInterface.js
import React, { useState, useEffect } from 'react';
import { Mic, MicOff, Camera, Volume2, Loader } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/Card';
import { Button } from '../components/ui/Button';
import { Alert, AlertDescription } from '../components/ui/Alert';

const SpeechInterface = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [response, setResponse] = useState('');
  const [error, setError] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('en-IN');
  const [lastCapturedImage, setLastCapturedImage] = useState(null);
  const [status, setStatus] = useState('checking'); // New 'checking' status
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    setStatus('checking');
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/status`);
      const data = await response.json();
      setStatus(data.status === 'Server is running' ? 'connected' : 'disconnected');
    } catch (err) {
      setStatus('disconnected');
      setError('Cannot connect to server');
    }
  };

  const handleStartRecording = async () => {
    setIsRecording(true);
    setError('');
    setIsLoading(true);

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/recognize`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ language: selectedLanguage }),
      });

      const data = await response.json();

      if (response.ok) {
        setResponse(data.response);
        setLastCapturedImage(`/api/placeholder/640/480?text=Last+Captured+Image&time=${Date.now()}`);
      } else {
        setError(data.error || 'Failed to recognize speech');
      }
    } catch (err) {
      setError('Failed to communicate with server');
    } finally {
      setIsRecording(false);
      setIsLoading(false);
    }
  };

  const handleStopRecording = () => {
    setIsRecording(false);
  };

  const languages = [
    { code: 'en-IN', name: 'English' },
    { code: 'hi-IN', name: 'हिंदी' },
    { code: 'mr-IN', name: 'मराठी' },
    { code: 'ta-IN', name: 'தமிழ்' },
    { code: 'bn-IN', name: 'বাংলা' },
    // Add more languages as needed
  ];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
      {/* Status Card */}
      <Card className="w-full max-w-md mb-4 shadow-md">
        <CardHeader>
          <CardTitle className="flex items-center justify-between text-xl">
            Smart Glasses Interface
            <span className={`px-3 py-1 rounded-full text-sm ${
              status === 'connected' ? 'bg-green-100 text-green-700' : status === 'checking' ? 'bg-yellow-100 text-yellow-700' : 'bg-red-100 text-red-700'
            }`}>
              {status === 'checking' ? <Loader className="w-4 h-4 animate-spin" /> : status}
            </span>
          </CardTitle>
        </CardHeader>
      </Card>

      {/* Camera Feed */}
      <Card className="w-full max-w-md mb-4 shadow-md">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-lg">
            <Camera className="w-5 h-5" />
            Camera Feed
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="aspect-video bg-gray-200 rounded-lg flex items-center justify-center text-gray-500">
            {lastCapturedImage ? (
              <img 
                src={lastCapturedImage} 
                alt="Last captured" 
                className="w-full h-full object-cover rounded-lg"
              />
            ) : (
              <span>No image captured</span>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Voice Controls */}
      <Card className="w-full max-w-md shadow-md">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-lg">
            <Volume2 className="w-5 h-5" />
            Voice Controls
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Language Selection */}
          <select
            value={selectedLanguage}
            onChange={(e) => setSelectedLanguage(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-lg"
          >
            {languages.map((lang) => (
              <option key={lang.code} value={lang.code}>
                {lang.name}
              </option>
            ))}
          </select>

          {/* Record Button */}
          <Button
            onClick={isRecording ? handleStopRecording : handleStartRecording}
            variant={isRecording ? "destructive" : "default"}
            className="w-full h-12 text-lg flex items-center justify-center gap-2"
            disabled={isLoading}
          >
            {isRecording ? (
              <>
                <MicOff className="w-6 h-6" />
                Stop Recording
              </>
            ) : (
              <>
                <Mic className="w-6 h-6" />
                {isLoading ? "Processing..." : "Start Recording"}
              </>
            )}
          </Button>

          {/* Error Message */}
          {error && (
            <Alert variant="destructive">
              <AlertDescription className="text-red-600 text-center">{error}</AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default SpeechInterface;
