import React, { useState, useEffect } from 'react';
import { Card, CardContent } from './Card';

const ImageGallery = ({ images, response, language, setLanguage }) => {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(language);

  // Function to handle speech synthesis (Bot speaking)
  const speakResponse = (text) => {
    const speechSynthesis = window.speechSynthesis;
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = selectedLanguage; // Use selected language
    utterance.onstart = () => setIsSpeaking(true);
    utterance.onend = () => setIsSpeaking(false);
    speechSynthesis.speak(utterance);
  };

  // Function to handle speech recognition (Bot listening)
  const startListening = () => {
    const recognition = new window.SpeechRecognition() || window.webkitSpeechRecognition;
    recognition.lang = selectedLanguage; // Set language for recognition
    recognition.onstart = () => setIsListening(true);
    recognition.onend = () => setIsListening(false);
    recognition.onresult = (event) => {
      const text = event.results[0][0].transcript;
      setTranscript(text);
      handleLanguageSelection(text); // If needed, update language based on input
    };
    recognition.start();
  };

  // Handle language selection from user's speech
  const handleLanguageSelection = (text) => {
    // Logic to handle language change based on speech (if needed)
    if (text.includes('English')) {
      setLanguage('en-US');
      setSelectedLanguage('en-US');
    } else if (text.includes('Spanish')) {
      setLanguage('es-ES');
      setSelectedLanguage('es-ES');
    }
    // Add more languages if needed
  };

  // Start listening when the component mounts (for first-time voice input)
  useEffect(() => {
    if (response) {
      speakResponse(response);
    }
  }, [response]);

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Recent Captures</h2>

      {/* Display selected language */}
      <div className="mb-4">
        <strong>Selected Language: </strong>{selectedLanguage}
      </div>

      {/* Display images */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {images && images.length > 0 ? (
          images.map((image) => (
            <Card key={image.id} className="overflow-hidden">
              <CardContent className="p-0">
                <img
                  src={`http://localhost:5002/api/image/${image.filename}`}
                  alt={`Captured at ${image.timestamp}`}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <div className="text-sm text-gray-500 mb-2">
                    {image.timestamp}
                  </div>
                  <p className="font-medium mb-2">Prompt: {image.prompt}</p>
                  <p className="text-sm text-gray-700">{image.response}</p>
                </div>
              </CardContent>
            </Card>
          ))
        ) : (
          <p>No images available.</p>
        )}
      </div>

      {/* Speech Animation */}
      <div className={`speaking-animation ${isSpeaking ? 'speaking' : ''}`}>
        {isSpeaking ? <span>ðŸŽ¤ Bot is Speaking...</span> : <span>Bot is silent.</span>}
      </div>

      {/* Speech-to-text (Real-time transcription) */}
      <div className="mt-4">
        <button onClick={startListening} disabled={isListening}>
          {isListening ? 'Listening...' : 'Start Listening'}
        </button>
        <p>Speech-to-Text: {transcript}</p>
      </div>
    </div>
  );
};

export default ImageGallery;
