// src/components/ui/Alert.js
import React from 'react';

export const Alert = ({ children, variant }) => {
  const alertClass =
    variant === 'destructive' ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700';
  return (
    <div className={`p-4 rounded-md ${alertClass}`}>
      {children}
    </div>
  );
};

export const AlertDescription = ({ children }) => <p>{children}</p>;
