import React, { useState, useEffect } from 'react';
import { Card, CardContent } from './Card';

const ImageGallery = ({ images, response, language, setLanguage }) => {
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [fetchedImages, setFetchedImages] = useState(images || []);
  const [selectedLanguage, setSelectedLanguage] = useState(language);

  const speakResponse = (text) => {
    const speechSynthesis = window.speechSynthesis;
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = selectedLanguage;
    utterance.onstart = () => setIsSpeaking(true);
    utterance.onend = () => setIsSpeaking(false);
    speechSynthesis.speak(utterance);
  };

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech Recognition API is not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = selectedLanguage;
    recognition.onstart = () => setIsListening(true);
    recognition.onend = () => setIsListening(false);
    recognition.onresult = (event) => {
      const text = event.results[0][0].transcript;
      setTranscript(text);
      handleLanguageSelection(text);
    };
    recognition.start();
  };

  const handleLanguageSelection = (text) => {
    if (text.includes('English')) {
      setLanguage('en-US');
      setSelectedLanguage('en-US');
    } else if (text.includes('Hindi')) {
      setLanguage('hi-IN');
      setSelectedLanguage('hi-IN');
    }
  };

  useEffect(() => {
    if (response) speakResponse(response);
    if (fetchedImages.length === 0) fetchImages();
  }, [response, fetchedImages]);

  const fetchImages = async () => {
    try {
      const res = await fetch('/api/images');
      const data = await res.json();
      if (data.images) {
        setFetchedImages(data.images);
      }
    } catch (error) {
      console.error("Failed to fetch images:", error);
    }
  };

  return (
    <div className="space-y-6 px-4 sm:px-6">
      <h2 className="text-2xl font-bold text-center">Recent Captures</h2>
      <div className="mb-4 text-center">
        <strong>Selected Language: </strong>{selectedLanguage}
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
        {fetchedImages.length > 0 ? (
          fetchedImages.map((image, index) => (
            <Card key={index}>
              <img
                src={`/api/image/${image.filename}`}
                alt={`Image captured at ${image.timestamp}`}
                className="w-full h-40 sm:h-48 object-cover rounded-md"
              />
              <CardContent>
                <p><strong>Prompt:</strong> {image.prompt}</p>
                <p><strong>Response:</strong> {image.response}</p>
                <p><strong>Time:</strong> {image.timestamp}</p>
              </CardContent>
            </Card>
          ))
        ) : (
          <p>No images available.</p>
        )}
      </div>
    </div>
  );
};

export default ImageGallery;
