// src/components/ui/Button.js
import React from 'react';

export const Button = ({ children, onClick, variant, className }) => {
  const variantClass =
    variant === 'destructive' ? 'bg-red-500 text-white' : 'bg-blue-500 text-white';
  return (
    <button
      onClick={onClick}
      className={`p-2 rounded-md ${variantClass} ${className}`}
    >
      {children}
    </button>
  );
};
